('fd37cdad-ea2c-4cf7-a043-a438a62d83ba', 'Advanced Yoga & Meditation Retreat', 'A full-day retreat to deepen your practice. Includes guided meditation, vinyasa flow, and a healthy lunch.', now() + interval '45 days 9 hours', now() + interval '45 days 16 hours', 'Serenity Wellness Center', 'https://placeholdr.ai/3a5152db-78f4-445c-8c78-edb9e81e40b8/512/512', 'Cultural'),
('59427676-0fc2-4cdf-ba09-b0b6d739bcf2', 'Learn to Code: Python for Beginners', 'A 4-week course covering the fundamentals of Python programming. No experience necessary.', now() + interval '14 days 18 hours', now() + interval '42 days 20 hours', 'Online / Zoom', 'https://placeholdr.ai/048a6f92-ee6f-4551-9477-101865e1f495/512/512', 'Workshop'),
('fd235e0b-928c-4539-a7c3-602734cf5626', 'Live Jazz Night', 'Featuring the cool tunes of the "Blue Notes Quartet". Enjoy great music and cocktails.', now() + interval '8 days 20 hours', now() + interval '8 days 23 hours', 'The Speakeasy Bar', 'https://placeholdr.ai/336c4f7e-2cd3-4544-83b9-b950444c5809/512/512', 'Cultural'),
('fd37cdad-ea2c-4cf7-a043-a438a62d83ba', 'Kids Summer Art Camp', 'A week of painting, sculpture, and crafts for children ages 6-10.', now() + interval '60 days', now() + interval '65 days', 'Community Art Studio', 'https://placeholdr.ai/4e9faa0a-2d8f-41a3-aa94-9c594cff3335/512/512', 'Social'),
('59427676-0fc2-4cdf-ba09-b0b6d739bcf2', 'Quarterly Town Hall Meeting', 'Join local leaders to discuss community planning, development, and resident concerns.', now() + interval '20 days 19 hours', now() + interval '20 days 21 hours', 'City Hall Auditorium', 'https://placeholdr.ai/dc8d4c01-271d-4570-a79a-0959079a7737/512/512', 'Community Meeting'),
('fd235e0b-928c-4539-a7c3-602734cf5626', 'Oktoberfest Celebration', 'Authentic German food, beer, and music. A festive celebration for the whole family!', now() + interval '50 days', now() + interval '50 days 8 hours', 'Cariari Park Pavilion', 'https://placeholdr.ai/6c96d100-85d6-4256-a2b4-c384fbc96479/512/512', 'Cultural'),
('fd37cdad-ea2c-4cf7-a043-a438a62d83ba', 'Italian Cooking Masterclass', 'Learn to make handmade pasta and classic sauces from Chef Isabella. An interactive and delicious experience.', now() + interval '25 days 18 hours', now() + interval '25 days 21 hours', 'The Culinary Institute', 'https://placeholdr.ai/50c3c4f8-6b09-4dc4-b40d-f6f50a6ab080/512/512', 'Educational'),
