INSERT INTO public.services (user_id, title, description, category, image_url, start_date, end_date) VALUES
('fd235e0b-928c-4539-a7c3-602734cf5626', 'Pro-Grade Lawn & Garden Care', 'Tired of spending your weekends doing yard work? We offer comprehensive lawn and garden maintenance. Services include precision mowing, edging, fertilization, weed control, and seasonal planting. Contact Carlos at 8456-7821 for a free estimate.', 'Offering', 'https://placeholdr.ai/32ee010e-a410-487c-95de-97bd9eb37eae/512/512', now(), now() + interval '7 days'),
('fd37cdad-ea2c-4cf7-a043-a438a62d83ba', 'Certified Babysitter / Childminder', 'Caring, responsible, and certified in First Aid and CPR. I have over 8 years of experience with children of all ages, from infants to pre-teens. Available for evening and weekend sitting. Email me at maria.babysitter@email.com to discuss your needs.', 'Offering', 'https://placeholdr.ai/8e715e1f-dfb2-4dbe-84e6-2aaf85541e07/512/512', now(), now() + interval '7 days'),
('59427676-0fc2-4cdf-ba09-b0b6d739bcf2', 'Math & Physics Tutoring (PhD Level)', 'Struggling with calculus or quantum mechanics? I can help. I hold a PhD in Physics and have a passion for making complex subjects understandable. I offer one-on-one tutoring. Call Dr. Rodriguez at 8734-9156 to schedule your first session.', 'Offering', 'https://placeholdr.ai/77bfa4f8-2e6a-41c4-8497-d449a321c876/512/512', now(), now() + interval '7 days'),
('fd235e0b-928c-4539-a7c3-602734cf5626', 'House Cleaner', 'Looking for a reliable and thorough house cleaner for a 3-bedroom home. Weekly service needed. Please contact me with your rates and references at ana.homecare@gmail.com.', 'Wanted', 'https://placeholdr.ai/10bac96d-6772-4cfb-a9c1-1b159861722e/512/512', now(), now() + interval '7 days'),
('fd37cdad-ea2c-4cf7-a043-a438a62d83ba', 'Reliable & Loving Pet Sitter', 'Going on vacation? I provide in-home pet sitting and daily dog walking. Your pets can stay in the comfort of their own home, reducing their stress. I''ll send you daily updates and photos! Contact Sofia at 8912-3456 to book your dates.', 'Offering', 'https://placeholdr.ai/2f651559-0de0-4ed1-8bcf-17881ab7f717/512/512', now(), now() + interval '7 days'),
('59427676-0fc2-4cdf-ba09-b0b6d739bcf2', 'Handyman for Hire', 'No job is too small! I can help with furniture assembly, mounting TVs, fixing leaky faucets, patching drywall, and other small home repairs and installations. Fast, friendly, and affordable service. WhatsApp Miguel at 8567-8901 for quick quotes.', 'Offering', 'https://placeholdr.ai/8560d477-f163-48e3-8492-2e53d11ade10/512/512', now(), now() + interval '7 days'),
('fd235e0b-928c-4539-a7c3-602734cf5626', 'Freelance Graphic & Web Design', 'Need a professional look for your business? I create beautiful logos, branding packages, marketing materials, and modern, responsive websites. Let''s collaborate to bring your vision to life. Portfolio and quotes available at design.cariari@protonmail.com.', 'Offering', 'https://placeholdr.ai/f5b1aa7c-188e-4383-9d26-7890fe209fff/512/512', now(), now() + interval '7 days'),
('fd37cdad-ea2c-4cf7-a043-a438a62d83ba', 'Certified Personal Fitness Trainer', 'Achieve your fitness goals! I offer personalized workout plans (in-person or virtual) and nutritional guidance. I specialize in weight loss, strength training, and functional fitness. Text or call Javier at 8234-5678 to start your fitness journey.', 'Offering', 'https://placeholdr.ai/d31fbd8a-0a34-4a29-ac94-f30f41a4eaad/512/512', now(), now() + interval '7 days'),
('59427676-0fc2-4cdf-ba09-b0b6d739bcf2', 'Gourmet Meal Prep Service', 'Busy professional looking for a weekly meal prep service that caters to a low-carb diet. Must use high-quality ingredients. Please send sample menus and pricing to chef.wanted.cr@outlook.com.', 'Wanted', 'https://placeholdr.ai/2a342ed6-5a30-486b-909e-13f89f9758e6/512/512', now(), now() + interval '7 days'),
('fd235e0b-928c-4539-a7c3-602734cf5626', 'Mobile Auto Detailing', 'I come to you! Full interior and exterior detailing for your car, truck, or SUV. Includes wash, wax, vacuum, and window cleaning to make your vehicle look brand new. Call Luis at 8789-0123 to schedule your appointment.', 'Offering', 'https://placeholdr.ai/5c79308e-baaf-412b-bd78-67d142bc5834/512/512', now(), now() + interval '7 days'),
('fd37cdad-ea2c-4cf7-a043-a438a62d83ba', 'Guitar Lessons for All Ages', 'Learn to play your favorite songs! I teach acoustic and electric guitar for all skill levels, from complete beginners to intermediate players looking to improve their technique. Contact Roberto at roberto.guitar.lessons@yahoo.com to schedule your first lesson.', 'Offering', 'https://placeholdr.ai/ac6ecf5a-420f-418e-94f1-d9be7695101f/512/512', now(), now() + interval '7 days'),
('59427676-0fc2-4cdf-ba09-b0b6d739bcf2', 'Event & Portrait Photography', 'Capturing your special moments. I specialize in lifestyle portraits, family photos, and event photography. My style is candid and natural. Contact me to discuss your photography needs at 8345-6789 or view my portfolio online.', 'Offering', 'https://placeholdr.ai/66a0115d-61ae-4bb6-b695-0010f57868f7/512/512', now(), now() + interval '7 days');
INSERT INTO public.notices (user_id, title, description, urgency) VALUES
('fd235e0b-928c-4539-a7c3-602734cf5626', '**URGENT** Wildfire Evacuation Warning', 'A wildfire is approaching the eastern side of the community. All residents in Zone 4 (east of Highway 12) are under an immediate evacuation order. Residents in Zone 3 should be prepared to evacuate at a moment''s notice. An emergency shelter is being established at the Cariari High School gymnasium. Please check the official city website for a map of evacuation zones and follow all instructions from emergency personnel.', 'high'),
('fd37cdad-ea2c-4cf7-a043-a438a62d83ba', 'Scheduled Water Main Maintenance', 'Please be advised that the city will be performing scheduled maintenance on the water main for the entire community. Water service will be interrupted on Tuesday, between 9:00 AM and 4:00 PM. We recommend storing enough water for your needs during this period. We apologize for the inconvenience.', 'medium'),
('59427676-0fc2-4cdf-ba09-b0b6d739bcf2', 'Community-Wide Garage Sale Weekend!', 'Get ready to find some treasures! The annual Cariari community garage sale is happening this weekend. Maps of participating homes will be available at the community center starting Friday morning.', 'low'),
('fd235e0b-928c-4539-a7c3-602734cf5626', 'Reminder: Pet Leash and Waste Policies', 'This is a friendly reminder that all pets must be on a leash at all times in public areas. Also, please be a good neighbor and clean up after your pets. Let''s keep our community clean and safe for everyone.', 'low'),
('fd37cdad-ea2c-4cf7-a043-a438a62d83ba', '**Nation-Wide Bulletin: New Public Health Guidelines**', 'The National Health Service has issued new guidelines regarding seasonal virus prevention. This comprehensive update includes recommendations for all citizens and businesses. Key points include: 1. **Vaccination**: All citizens over the age of 5 are encouraged to receive the latest seasonal vaccine. Mobile clinics will be available. 2. **Hygiene**: Increased emphasis on hand washing and sanitation in public spaces. 3. **Ventilation**: Businesses are advised to ensure adequate indoor air circulation. 4. **Symptom Monitoring**: Citizens are asked to self-monitor for symptoms and test if they feel unwell. For full details, please consult the official government health portal. This bulletin is for informational purposes to ensure our community stays informed and prepared.', 'medium'),
('59427676-0fc2-4cdf-ba09-b0b6d739bcf2', 'Community Pool Opens for the Season', 'Great news! The community pool will officially open for the summer season on Saturday. Please review the updated pool hours and safety rules posted at the entrance.', 'low'),
('fd235e0b-928c-4539-a7c3-602734cf5626', 'High Wind Advisory in Effect', 'A high wind advisory is in effect until tomorrow evening. Please secure any loose outdoor items, such as patio furniture and garbage cans, to prevent them from blowing away and causing damage.', 'high'),
('fd37cdad-ea2c-4cf7-a043-a438a62d83ba', 'Holiday Waste Collection Schedule Change', 'Due to the upcoming national holiday, all waste and recycling collection will be delayed by one day for the entire week.', 'medium'),
('59427676-0fc2-4cdf-ba09-b0b6d739bcf2', 'Volunteers Needed for Park Cleanup Day', 'We are looking for volunteers to help with our annual park cleanup day. It''s a great way to meet neighbors and help keep our park beautiful. Gloves and bags will be provided.', 'medium'),
('fd235e0b-928c-4539-a7c3-602734cf5626', 'Beware of Phone Scams Targeting Residents', 'We have received reports of phone scams where callers are pretending to be from the city utility department and demanding immediate payment. The city will never demand payment over the phone. Please hang up and report such calls.', 'high'),
('fd37cdad-ea2c-4cf7-a043-a438a62d83ba', 'Monthly Street Sweeping Schedule', 'This is a reminder that street sweeping occurs on the third Thursday of every month. Please ensure your vehicle is moved to avoid a ticket.', 'low'),
('59427676-0fc2-4cdf-ba09-b0b6d739bcf2', 'Meet Your New Community Association Board', 'The Community Association election results are in. Please join us in welcoming the new board members at a meet-and-greet event next Wednesday at the community hall.', 'low');